INSERT INTO `facilino_version` (`id`, `version`, `name`, `description`, `image`, `descripcion`) VALUES
(1, 'Facilino', 'Facilino', 'Facilino allows you to upload code to your selected board via USB cable. You need to locally run Facilino service that will allow you to compile and upload code. You can also compile the code in Arduino IDE or similar by your own.', 'facilino.png', 'Facilino te permite subir c&oacute;digo a la tarjeta seleccionada a trav&eacute;s del cable USB. Necesitas ejecutar localmente servicio de Facilino para compilar y subir el c&oacute;digo por ti. Puedes tambi&eacute;n copiar el c&oacute;digo a Arduino IDE o similar y compilarlo por tu cuenta.'),
(2, 'FacilinoJunior', 'Facilino Junior', 'Facilino Junior includes a basic instruction set, compared to standard Facilino version. Code is also expected to be uploaded via USB (you need Facilino web service) or you can compile it by your own with Arduino IDE or similar.', 'facilino_junior.png', 'Facilino Junior incluye un conjunto de instrucciones b&aacute;sicas, en comparaci&oacute;n con la versi&oacute;n est&aacute;ndar. Tambi&eacute;n se puede subir el c&oacute;digo a trav&eacute;s de USB (con el servicio web Facilino) o puede compilarlo por su cuenta con Arduino IDE o similar.\r\n\r\n'),
(3, 'FacilinoOTA', 'Facilino OTA', 'Facilino OTA (Over-The-Air) allows you to upload code via WiFi. Before uploading the code, you will be prompted to provide your device IP. The device needs a firmware that must be installed (beforehand only the first time) via USB.', 'facilino_ota.png', 'Facilino OTA (Over-The-Air) permite cargar c&oacute;digo a trav&eacute;s de WiFi. Debes proporcionar la IP del dispositivo antes de subir el c&oacute;digo. El dispositivo necesita, adem&acute;s un firmware que debe instalarse la primera vez a trav&eacute;s de USB.');
INSERT INTO `filters` (`id`, `name`, `title`, `description`, `image`, `titulo`, `descripcion`) VALUES
(1, 'Generic', 'Generic Project', 'Complete instruction set with for all type of projects. Make your dreams become true with the full instruction set.', 'advanced_electronics.jpg', 'Proyecto Gen&eacute;rico', 'Conjunto completo de instrucciones para todo tipo de proyectos. Haz realidad tus sue&ntilde;os con el conjunto completo de instrucciones.'),
(2, 'DYOR', 'DYOR Robot', 'Block instruction set for low-cost Arduino-based wheeled robots to generate movements, sounds and expressions.', 'DYOR_robot1.jpg', 'Robot DYOR', 'Conjunto de instrucciones de bloque para robots de ruedas basados en Arduino de bajo coste para generar movimientos, sonidos y expresiones.'),
(3, 'bPED', 'bPED Robot', 'Block instruction set for hipless walking robots like bPED to generate movements, sounds and expressions.', 'bPED.jpeg', 'Robot bPED', 'Conjunto de instrucciones de bloque para robots que caminan sin caderas como bPED, para generar movimientos, sonidos y expresiones.'),
(4, 'meArm', 'meArm Robot', 'Block instruction set for a robot arm with three servos and a gripper. It includes instructions to controlling the robot in several modes.', 'meArm.jpg', 'Robot meArm', 'Conjunto de instrucciones de bloque para un brazo rob&oacute;tico con tres servos y una pinza. Incluye instrucciones para controlar el robot en varios modos.'),
(5, 'Multisensor', 'Multisensor Board', 'Block instruction set for using the multisensor shield. It includes instructions to learn how to read sensors or activate outputs with the multisensor board.', 'multisensor.png', 'Placa Multisensor', 'Conjunto de instrucciones de bloque para usar la tarjeta multisensor. Incluye instrucciones para aprender a leer informaci&oacute;n o activar salidas de la tarjeta multisensor.'),
(6, 'HomeAutomation', 'DIY Home Automation Kit', 'Block instruction set for enviromental sensors used in home automation, intelligent greenhouse, weather station projects.', 'home_automation.jpg', 'Kit de Dom&oacute;tica DIY', 'Conjunto de instrucciones de bloque para sensores ambientales utilizados en proyectos de dom&oacute;tica, invernaderos inteligentes y estaciones meteorol&oacute;gicas.'),
(7, 'T-Watch2020', 'T-Watch 2020', 'Block instruction set for programming T-Watch 2020. It includes instructions for configuring your watch user interface and recognising gestures.', 'twatch.png', 'T-Watch 2020', 'Conjunto de instrucciones de bloque para programar el reloj inteligente T-Watch 2020. Incluye instrucciones para configurar la interfaz del reloj y reconocer gestos.');
